import React, { useState } from 'react';
import Gasto from "./Gasto";
import PropTypes from "prop-types";


export default function Listado(props) {
    const { gastos } = props;

    const [actualizado, setActualizado] = useState([gastos]);

    useEffect(() => {

    }, [input])
    const eliminar = (id) => {
        const actualizadas = gastos.filter(gasto => gasto.id !== id)
        setActualizado(actualizadas);
    }


    return (
        < div className="gastos-realizados" >
            <h2>Listado</h2>
            {
                gastos.map(actualizado => (
                    <Gasto
                        key={actualizado.id}
                        actualizado={actualizado}
                        eliminar={eliminar}
                    />
                ))
            }
        </div >
    )
}



Listado.propTypes = {
    gastos: PropTypes.array.isRequired
}
